::selection {
    background: rgba(240, 145, 153, .4);
}
*,
small.feed,
textarea,
span.avatarNeue {
    font-family: roboto, "思源黑体", sans-serif;
}
#navMenuNeue
li,
#navMenuNeue li a span,
.tl_relation a,
.tl_group a,
.tl_wiki a,
.tl_index a,
.tl_blog a {
    transition: all 0ms !important;
}
body.bangumiNeue,
body.bangumi,
body {
    background-color: #f1f1f1;
}
#headerSubject h1 {
    background: #fff;
    margin: auto;
    line-height: 70px;
    width: 85%;
    padding-left: 15%;
}
#headerNeue2,
#navNeue2 #navMenuNeue li a.chl,
.progress
.inner {
    background: transparent url(http://bgm.tv/img/rc2/bg_header_v2.png) repeat-x;
}
#headerNeue2 a.logo {
    background: transparent url(http://bgm.tv/img/rc2/logo_rc2_m.png) left 50% no-repeat;
}
.idBadgerNeue .avatar span.avatarNeue {
    background-image: url(data:image/svg+xml;base64,) !important;
}
span.avatarNeue {
    border-radius: 50%;
    -webkit-transition: all linear 100ms;
    -moz-transition: all linear 100ms;
    transition: all linear 100ms;
}
#comment_list div.light_even,
div#comment_list div.light_odd,
ul.browserMedium,
ul.browserCoverMedium,
#entry_list
div.item,
#columnA #blog,
#columnA #index,
#columnA #group,
#columnA #friend,
#columnA #mono,
#columnA #anime,
#columnA #book,
#columnA #game,
#columnA #music,
#columnA #real,
#prgManager,
.TsukkmiBox,
.timelineTabs,
ul.browserList li.item,
ul.coversSmall,
ul.browserCoverSmall {
    background: #fff !important;
    border-radius: 2px;
    padding: 25px;
    transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
    margin-bottom: 14px;
    border-collapse: collapse;
}
#comment_list div.light_even:hover,
div#comment_list div.light_odd:hover,
ul.browserMedium:hover,
ul.browserCoverMedium:hover,
#entry_list
div.item:hover,
#columnA #blog:hover,
#columnA #index:hover,
#columnA #group:hover,
#columnA #friend:hover,
#columnA #mono:hover,
#columnA #anime:hover,
#columnA #book:hover,
#columnA #game:hover,
#columnA #music:hover,
#columnA #real:hover,
#prgManager:hover,
.TsukkmiBox:hover,
.timelineTabs:hover,
ul.browserList li.item:hover,
ul.coversSmall:hover,
ul.browserCoverSmall:hover{
    box-shadow: 0 2px 8px 0 rgba(0, 0, 0, 0.14);
}
#comment_list div.light_even:active,
div#comment_list div.light_odd:active,
ul.browserMedium:active,
ul.browserCoverMedium:active,
#entry_list
div.item:active,
#columnA #blog:active,
#columnA #index:active,
#columnA #group:active,
#columnA #friend:active,
#columnA #mono:active,
#columnA #anime:active,
#columnA #book:active,
#columnA #game:active,
#columnA #music:active,
#columnA #real:active,
#prgManager:active,
.TsukkmiBox:active,
.timelineTabs:active,
ul.browserList li.item:active,
