@font-face {
	font-family: "Open Sans";
	font-style: normal;
	font-weight: 400;
	src: url(https://fonts.gstatic.com/s/opensans/v15/mem8YaGs126MiZpBA-UFW50bbck.woff2) format("woff");
}

@font-face {
	font-family: "Material Icons";
	src: url(https://fonts.gstatic.com/s/materialicons/v38/flUhRq6tzZclQEJ-Vdg-IuiaDsNc.woff2) format("woff2"),
		 url(http://onox.qiniudn.com/maverick/materialicons_safari_v38.woff) format("woff");
}

@keyframes eventoff_duration {
	from {
		pointer-events: auto;
	}

	to {
		pointer-events: none;
	}
}

@keyframes eventon_duration {
	from {
		pointer-events: none;
	}

	to {
		pointer-events: auto;
	}
}

@keyframes button_effect_left {
	from {
		opacity: 0;
		transform: translateX(80%);
	}

	to {
		opacity: 1;
		transform: translateX(0);
	}
}

@keyframes button_effect_right {
	from {
		opacity: 0;
		transform: translateX(-90%);
	}

	to {
		opacity: 1;
		transform: translateX(0);
	}
}

@keyframes rotating {
	from {
		transform: rotate(0deg);
	}

	to {
		transform: rotate(360deg);
	}
}

@keyframes rotate-plane {
	0%{ 
		transform: perspective(120px) rotateX(0deg) rotateY(0deg);
	}
	50%{ 
		transform: perspective(120px) rotateX(-180.1deg) rotateY(0deg);
	}
	100%{ 
		transform: perspective(120px) rotateX(-180deg) rotateY(-179.9deg);
	}
}

