/*气泡提示*/
.ui_bubble_content {
	position: absolute !important;
	border: none !important;
	left: 0 !important;
	background: var(--m-ecolor) !important;
	border-radius: 6px !important;
	padding: 5px !important;
	padding-left: 7px !important;
	box-shadow: 0 2px 4px 0 rgba(0,0,0,0.08), 0 2px 8px 0 rgba(0,0,0,0.08) !important;
	color: #fff !important;
	letter-spacing: 1px;
}

.ui_bubble_up {
	transform: translate(-100%, -100%) !important;
	top: 0 !important;
}

.ui_bubble_down {
	transform: translate(-100%, 50%) !important;
	bottom: 0 !important;
}

.ui_bubble_content * {
	color: #fff !important;
}

.ui_triangle {
	position: absolute !important;
	left: 0 !important;
	width: 0 !important;
	height: 0 !important;
	border-top: 6px solid transparent;
	border-left: 8px solid var(--m-ecolor);
	border-bottom: 6px solid transparent;
}

.ui_triangle_up {
	transform: translateY(-100%);
	margin-top: -10px !important;
	bottom: 0 !important;
}

.ui_triangle_down {
	transform: translateY(100%);
	margin-top: 10px !important;
	bottom: 0 !important;
}

.ui_bubble_wrap .close_msg_tip,
.ui_bubble_closed {
	background: none !important;
}

.ui_bubble_wrap .close_msg_tip:before,
.ui_bubble_closed:before {
	content: "X";
	font-weight: bold;
	display: block;
	transform: translateY(-2px) scaleY(.8);
}

#com_userbar_message.ui_bubble_wrap {
	z-index: 10004;
	position: fixed !important;
	width: 90px !important;
	right: 0 !important;
	top: 0 !important;
}

#com_userbar_message .ui_bubble_up {
	top: 42px !important;
	transform: translateX(-100%) !important;
	background-color: var(--m-ecolor) !important;
}

#com_userbar_message .ui_triangle_up {
	top: 48px !important;
	transform: none !important;
	margin-top: 0 !important;
}

