/*1-3 主布局*/
.mn{display:flex;flex-direction:column}
	/*1-3-1 顶栏*/
	#hd .wp{display:flex;position:fixed;top:0;width:100%;height:36px;background:var(--pri);padding:0;z-index:200}
		/*1-3-1-1 快速导航*/
		#nv{display:flex;width:auto!important;height:auto!important;position:static!important;opacity:1!important;border:0;background:0}
		#qmenu{order:0;background:0;height:auto;width:auto;line-height:34px;margin:0;padding:0 4px 0 12px;font-size:15px;white-space:nowrap;flex-shrink:0}
		#qmenu:hover{color:var(--tl)}
		#qmenu:after{content:" ▼";font-size:13px}
		#qmenu_menu{position:fixed!important;left:0!important;top:37px!important;border-color:var(--pri);box-shadow:none;width:auto;height:calc(100%)!important;padding:0}
		#qmenu_menu ul.nav a{border-radius:0;margin:0;padding:36px 0 0;background-size:46%;background-position-y:8px}
		#qmenu_menu ul.nav a:hover{background-color:var(--pri)}
		#fjump_menu{max-height:100%}
		#hd .wp > .h_pop{position:fixed!important;top:36px!important}
		.btda{border:0}
		.jump_bdl{display:flex}
		.jump_bdl .bbda{background:var(--pri);border:0}
		.jump_bdl li{background:var(--bg);border-color:var(--prid);padding:0;width:auto;min-width:150px;height:calc(100vh - 146px)}
		.jump_bdl p{padding:0 12px}
		.jump_bdl p:hover{background:var(--pri)}
		.jump_bdl p a{padding:0}
		.jump_bdl .a a, .jump_bdl .a a:hover,.jump_bdl p a:hover{background:0}
		#nv ul{display:flex;flex-grow:1;justify-content:flex-start}
		#nv li{background:none;height:auto;line-height:35px}
		#nv li a{color:var(--t);background:none!important;text-decoration:none;padding:0 8px;width:calc(100% - 16px);height:32px;text-align:center;white-space:nowrap}
		#nv li.a{background-color:var(--prid)}
		/*1-3-1-2 搜索栏*/
		#scbar{order:0;height:auto;border:0;line-height:normal;margin:0;padding:2px 4px;flex-grow:1;flex-shrink:0}
		#scbar_form>table>tbody>tr{display:flex;border:1px solid var(--ic);background-color:var(--bg);margin:2px 8px;height:26px}
			/*1-3-1-2-1 搜索类型*/
			.scbar_type_td{order:0;background:none;width:48px}
			#scbar_type{line-height:26px;padding-left:8px}
			#scbar_type:after{content:" ▼";font-size:9px;vertical-align:top}
			#scbar_type_menu{margin-top:0px;padding:0;width:48px;min-width:0;top:33px!important;border-color:var(--ic);border-top:0;box-shadow:none;margin-left:-1px}
			#scbar_type_menu li{width:48px}
			#scbar_type_menu li a{padding: 2px 0 2px 10px}
			/*1-3-1-2-2 搜索文本*/
			.scbar_txt_td{order:1;background:0}
			#scbar_txt{padding:0;background:0;width:auto;max-width:120px;height:100%;border:none;text-indent:4px}
			/*1-3-1-2-3 搜索按钮*/
			.scbar_btn_td{order:2;background:0;width:auto}
			#scbar_btn{height:27px;width:32px}
			#scbar_btn strong{display:none}
