	.sltm li.current{color:var(--sec)}
	#myprompt_menu{position:fixed!important;top:32px!important}
	/*1-4-9 浮动小工具栏*/
	#scrolltop{background-color:var(--bg);border-color:var(--pri);opacity:.6;z-index:199}
