		.pm .c{background-color:var(--bg)}
		.pm .flb{background:var(--pri)}
		.pm .flb em{color:var(--t);text-shadow:none;background:0;padding:0}
		.pm_tac{background:var(--pril);border:0}
		.pmfm{padding:0 8px 8px}
		.pmfm .tedt{width:auto}
		.pmm .pmd{background:var(--pril);box-shadow:none;border-color:var(--pri);border-radius:6px 6px 0 6px}
		.pmm .pmt{background:0;width:0;height:0;border:3px solid;border-color:transparent transparent var(--pri) var(--pri)}
a[onclick="refreshMsg();"]:before{display:inline-block;content:"⭯";color:var(--sec);font-size:16px;line-height:10px}
	/*1-4-11 双色表格*/
