#um{margin-right:96px}/**/

/*12 粗大首页*//*
.fl.bm > .bm.bmw.cl > .bm_h.cl{width:140px;}
.fl.bm > .bm.bmw.cl > .bm_h.cl > h2{font-size:16px;height:36px;line-height:34px;border-radius:0 0 16px 0}
div[id^="category_"]{font-size:18px;width:calc(100% - 120px);padding:12px 0!important}
div[id^="category_"] .fl_tb td{padding:12px 20px}
div[id^="category_"] .fl_g dt{width:calc((100vw - 460px)/4);min-width:140px}
div[id^="category_"] .fl_g dt:before,div[id^="category_"] h2:before{margin-right:8px}
.bm.lk .x li{padding:12px 20px}
.bm.lk:before{width:138px;min-width:138px;height:28px;line-height:28px;font-size:16px;border-radius:0 0 16px 0;margin-bottom:12px}
#online h3 > strong{width:138px;font-size:16px;line-height:27px;text-align:center;margin-bottom:30px}
#online span{padding:9px 12px 9px 20px!important}
#online .xs1{padding:0 12px;line-height:48px;font-size:18px!important}/**/

/*13 反色涂黑识别*/
#postlist font[color="black"],font[color="#000000"],
#postlist font[style="color:rgb(0, 0, 0)"]{color:var(--icddd)!important}
#postlist font[style="background-color:black"],
#postlist font[style="background-color:#000000"],
#postlist font[style="background-color:rgb(0, 0, 0)"]{background-color:var(--icddd)!important}
#postlist font[color="white"],
#postlist font[color="#ffffff"],
#postlist font[style="color:rgb(244, 244, 244)"],
#postlist font[style="color:rgb(255, 255, 255)"],
#postlist font[color="lemonchiffon"]{color:var(--bg)!important}
#postlist font[style="background-color:white"],
#postlist font[style="background-color:#ffffff"],
#postlist font[style="background-color:rgb(244, 244, 244)"],
#postlist font[style="background-color:rgb(255, 255, 255)"],
#postlist font[style="background-color:lemonchiffon"]{background-color:transparent!important}
#postlist font[color="yellow"],
#postlist font[color="#ffff00"]{color:var(--icl)!important}
#postlist font[style="background-color:yellow"],
#postlist font[style="background-color:#ffff00"]{background-color:var(--icl)!important}
#postlist font[color="#191919"],
#postlist font[color="#222222"],
#postlist font[color="#333333"],
#postlist font[style="color:rgb(43, 43, 43)"],
#postlist font[style="color:rgb(51, 51, 51)"],
#postlist font[style="color:rgb(64, 64, 64)"],
#postlist font[style="color:rgb(85, 85, 85)"]{color:var(--t)!important}
#postlist font[style="background-color:#191919"],
#postlist font[style="background-color:#222222"],
#postlist font[style="background-color:#333333"],
#postlist font[style="background-color:rgb(43, 43, 43)"],
#postlist font[style="background-color:rgb(51, 51, 51)"],
#postlist font[style="background-color:rgb(64, 64, 64)"],
#postlist font[style="background-color:rgb(85, 85, 85)"]{background-color:var(--t)!important}
/**/
